mapper.update:
update tb_rent

    StockOutWarehouseId=#{StockOutWarehouseId},
    StockInWarehouseId=#{StockInWarehouseId},
    billDate=#{billDate},
    totalStockIn=#{totalStockIn}

where id=#{id}

mapper.select:
SELECT stock.*,w_o.name stockOutWarehouseName,w_i.name StockInWarehouseName,d.textField billStatName FROM tb_stockout stock LEFT JOIN tb_warehouse w_o ON stock.StockOutWarehouseId = w_o.id LEFT JOIN tb_warehouse w_i ON stock.StockInWarehouseId = w_i.id LEFT JOIN (SELECT valueField, textField FROM tb_dic WHERE keyWord = 'stockOutStatus') d ON stock.billStat = d.valueField
and billStat=#{billStat}
limit #{start},#{size}

select count(*) from tb_rent


        and name like #{name}


        and code like #{code}
